import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './HabitDetails.css';  // Import the CSS file

const HabitDetails = () => {
  const { id } = useParams();
  const [habit, setHabit] = useState(null);

  useEffect(() => {
    fetch(`/api/habits/${id}`)
      .then(response => response.json())
      .then(data => setHabit(data));
  }, [id]);

  if (!habit) return <div className="loading">HabitsLoading...</div>;

  return (
    <div className="habit-details-container">
      <div className="habit-details-content">
        <h1>{habit.name}</h1>
        <p>{habit.description}</p>
        <h2>Progress</h2>
        <ul className="progress-list">
          {habit.progress.map(prog => (
            <li key={prog.id} className="progress-item">{prog.date}: {prog.status}</li>
          ))}
        </ul>
        <h2>Reminders</h2>
        <ul className="reminders-list">
          {habit.reminders.map(rem => (
            <li key={rem.id} className="reminder-item">{rem.time}: {rem.frequency}</li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default HabitDetails;
