import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import './HabitDetails.css';

const HabitDetails = () => {
  const { id } = useParams();
  const [habit, setHabit] = useState(null);

  useEffect(() => {
    fetch('/habits.json')
      .then(response => response.json())
      .then(data => {
        const habitData = data.habits.find(h => h.id === parseInt(id));
        setHabit(habitData);
      });
  }, [id]);

  if (!habit) return <div>Loading...</div>;

  return (
    <div>
      <h1>{habit.name}</h1>
      <p>{habit.description}</p>
      <h2>Progress</h2>
      <ul>
        {habit.progress.map(prog => (
          <li key={prog.id}>{prog.date}: {prog.status}</li>
        ))}
      </ul>
      <h2>Reminders</h2>
      <ul>
        {habit.reminders.map(rem => (
          <li key={rem.id}>{rem.time}: {rem.frequency}</li>
        ))}
      </ul>
    </div>
  );
};

export default HabitDetails;
